mapper.select:
select e.`id`, e.`name`, e.`gender`, e.`birthday`, e.`idCard`, e.`wedlock`, e.`nationId`, e.`nativePlace`, e.`politicsId`, e.`email`, e.`phone`, e.`address`, e.`departmentId`, e.`jobLevelId`, e.`positionId`, e.`engageForm`, e.`highestDegree`, e.`major`, e.`school`, e.`employmentDate`, e.`workStatus`, e.`workId`, e.`contractTerm`, e.`conversionDate`, e.`dimissionDate`, e.`beginContractDate`, e.`endContractDate`, e.`seniority`, e.`deleted`, n.`name` nName, p.`name` pName, d.`name` dName, j.`name` jName, pos.`name` posName from employee e inner join nation n on e.`nationId` = n.`id` inner join politicsstatus p on e.`politicsId` = p.`id` inner join department d on e.`departmentId` = d.`id` inner join joblevel j on e.`jobLevelId` = j.`id` inner join position pos on e.`positionId` = pos.`id`
and e.`deleted` = false
and e.`name` like CONCAT('%',#{search.name},'%')
and e.`nationId` = #{search.nationId}
and e.`politicsId` = #{search.politicsId}
and e.`departmentId` = #{search.departmentId}
and e.`jobLevelId` = #{search.jobLevelId}
and e.`positionId` = #{search.positionId}
and e.`engageForm` = #{search.engageForm}
and (e.`employmentDate` between #{search.employmentDateScope[0]} and #{search.employmentDateScope[1]})
and e.`workStatus` = #{search.workStatus}
order by e.`id` ASC

select e.`id`, e.`name`, e.`gender`, e.`birthday`, e.`idCard`, e.`wedlock`, e.`nativePlace`, e.`email`, e.`phone`, e.`address`, e.`engageForm`, e.`highestDegree`, e.`major`, e.`school`, e.`employmentDate`, e.`workStatus`, e.`workId`, e.`contractTerm`, e.`conversionDate`, e.`dimissionDate`, e.`beginContractDate`, e.`endContractDate`, e.`seniority`, e.`deleted`, n.`name` nName, p.`name` pName, d.`name` dName, j.`name` jName, pos.`name` posName from employee e inner join nation n on e.`nationId` = n.`id` inner join politicsstatus p on e.`politicsId` = p.`id` inner join department d on e.`departmentId` = d.`id` inner join joblevel j on e.`jobLevelId` = j.`id` inner join position pos on e.`positionId` = pos.`id`
and e.`deleted` = false
and e.`name` like CONCAT('%',#{search.name},'%')
and e.`nationId` = #{search.nationId}
and e.`politicsId` = #{search.politicsId}
and e.`departmentId` = #{search.departmentId}
and e.`jobLevelId` = #{search.jobLevelId}
and e.`positionId` = #{search.positionId}
and e.`engageForm` = #{search.engageForm}
and (e.`employmentDate` between #{search.employmentDateScope[0]} and #{search.employmentDateScope[1]})
and e.`workStatus` = #{search.workStatus}
order by e.`id` ASC
limit #{offset}, #{exportPageSize}
